import React, { useState } from 'react'
import '../../resources/css/LoginFormModal.css'

const countryCodes = [
    { code: 'RU', name: 'Russia', dialCode: '7' },
    { code: 'US', name: 'USA', dialCode: '1' },
    { code: 'BY', name: 'Belarus', dialCode: '375' },
    { code: 'GB', name: 'UK', dialCode: '44' },
    // Добавьте другие страны по необходимости
  ];

export const LoginFormModal = () => {
    const [inputFullName, setInputFullName] = useState<string>('');
    const [selectedCountry, setSelectedCountry] = useState(countryCodes[0].dialCode); // Код страны по умолчанию
    const [phoneNumber, setPhoneNumber] = useState(''); // Хранение номера телефона
    const [inputEmail, setInputEmail] = useState<string>('');
    // const [modalOpen, setModalOpen] = useState<boolean>(false);

    // const toggleMenu = () => {
    //     setModalOpen(!modalOpen);
    // };

    const handleCountryChange = (event: React.ChangeEvent<HTMLSelectElement>): void => {
        setSelectedCountry(event.target.value);
      };
    
    const handlePhoneNumberChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setPhoneNumber(event.target.value);
    };

    async function fetchData(event: any) {
        event.preventDefault();
        try {
            console.log(process.env.REACT_APP_SERVER_URL)
            const response = await fetch(process.env.REACT_APP_SERVER_URL + '/sendMail', {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify({
                    name: inputFullName,
                    phone: (selectedCountry + phoneNumber).toString(),
                    mail: inputEmail
                })
            });
            const data = await response.json();

            if (!response.ok) {
                console.error(data.message);
                return;
            }

            window.location.reload();
        } catch (error: any) {
            console.error(error.message);
        }
    }

  return (
    <>
        <div classNa className={`login-form-modal-container ${menuOpen ? 'active' : ''}`}>
            <h3>Get in touch with us</h3>
            <form onSubmit={fetchData}>
                <div className='form-inputs'>
                    <div className="input-block">
                        <label htmlFor='inputLoginFullName'>Full Name</label>
                        <input type="text" placeholder='Full name' className='input-field' id='inputLoginFullName' required value={inputFullName} onChange={(e) => setInputFullName(e.target.value)}/>
                    </div>
                    <div className="input-block">
                        <label htmlFor='inputLoginPhone'>Phone</label>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '11px' }}>
                            <select
                                value={selectedCountry}
                                onChange={handleCountryChange}
                                className='select-phone-country'
                                required
                            >
                                {countryCodes.map((country) => (
                                    <option key={country.code} value={country.dialCode}>
                                        {country.dialCode}
                                    </option>
                                ))}
                            </select>
                            <input
                                type="tel"
                                className='input-field'
                                value={phoneNumber}
                                onChange={handlePhoneNumberChange}
                                placeholder="Number"
                                required
                            />
                        </div>
                    </div>
                    <div className="input-block">
                        <label htmlFor='inputLoginFullName'>Email</label>
                        <input type="text" placeholder='Email' className='input-field' id='inputLoginFullName' required value={inputEmail} onChange={(e) => setInputEmail(e.target.value)}/>
                    </div>
                </div>
                <button className='submit-btn'>Submit now</button>
            </form>
        </div>
        
    </>
  )
}
