import React, { useState } from 'react'
import '../../resources/css/LoginForm.css'
import { PhoneInput } from 'react-international-phone';
import 'react-international-phone/style.css';


export const LoginForm = () => {
    const [inputFullName, setInputFullName] = useState<string>('');
    const [phoneNumber, setPhoneNumber] = useState(''); // Хранение номера телефона
    const [inputEmail, setInputEmail] = useState<string>('');
    const [inputCompany, setInputCompany] = useState<string>('');
    const [errors, setErrors] = useState<{ fullName?: boolean; phone?: boolean; email?: boolean; company?: boolean }>({});

    // Валидация при отправке формы
    const validateForm = () => {
        const newErrors: { fullName?: boolean; phone?: boolean; email?: boolean; company?: boolean } = {};
        console.log(inputFullName)
        console.log(!inputFullName)
        console.log(inputFullName === '')
        if (!inputFullName) newErrors.fullName = true;
        if (!phoneNumber) newErrors.phone = true;
        if (!inputEmail) newErrors.email = true;
        if (!inputCompany) newErrors.company = true;
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleFieldChange = (field: string, value: string) => {
        switch (field) {
            case 'fullName':
                setInputFullName(value);
                setErrors(prev => ({ ...prev, fullName: false }));
                break;
            case 'phone':
                setPhoneNumber(value);
                setErrors(prev => ({ ...prev, phone: false }));
                break;
            case 'email':
                setInputEmail(value);
                setErrors(prev => ({ ...prev, email: false }));
                break;
            case 'company':
                setInputCompany(value);
                setErrors(prev => ({ ...prev, company: false }));
                break;
            default:
                break;
        }
    };


    async function fetchData(event: any) {
        event.preventDefault();
        if (!validateForm()) {
            return; // Останавливаем отправку, если есть ошибки
        }
        try {
            console.log(process.env.REACT_APP_SERVER_URL)
            const response = await fetch(process.env.REACT_APP_SERVER_URL + '/sendMail', {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify({
                    name: inputFullName,
                    phone: phoneNumber,
                    mail: inputEmail,
                    companyName: inputCompany
                })
            });
            const data = await response.json();

            if (!response.ok) {
                console.error(data.message);
                return;
            }

            window.location.reload();
        } catch (error: any) {
            console.error(error.message);
        }
    }

  return (
    <>
        <div className='login-modal-container'>
            <h3>Get in touch with us</h3>
            <form onSubmit={fetchData}>
                <div className='form-inputs'>
                    <div className="input-block">
                        <label htmlFor='inputLoginFullName'>Full name</label>
                        <input type="text" placeholder='Full name' className={`input-field ${errors.fullName ? 'input-error' : ''}`}  id='inputLoginFullName' value={inputFullName} onChange={(e) => handleFieldChange('fullName', e.target.value)}/>
                    </div>
                    <div className="input-block">
                        <label htmlFor='inputLoginPhone'>Phone</label>
                        <PhoneInput
                            defaultCountry="gb"
                            value={phoneNumber}
                            onChange={(phoneNumber) => handleFieldChange('phone', phoneNumber)}
                            style={{height: '45px'}}
                            className={`${errors.fullName ? 'phone-input-error' : ''}`} 
                        />
                    </div>
                    <div className="input-block">
                        <label htmlFor='inputLoginFullName'>Email</label>
                        <input type="text" placeholder='Email' className={`input-field ${errors.fullName ? 'input-error' : ''}`}  id='inputLoginFullName' value={inputEmail} onChange={(e) => handleFieldChange('email', e.target.value)}/>
                    </div>
                    <div className="input-block">
                        <label htmlFor='inputLoginFullName'>Your company name</label>
                        <input type="text" placeholder='Company name' className={`input-field ${errors.fullName ? 'input-error' : ''}`}  id='inputLoginCompanyName' value={inputCompany} onChange={(e) => setInputCompany(e.target.value)}/>
                    </div>
                </div>
                <button className='submit-btn'>Submit now</button>
            </form>
        </div>
        
    </>
  )
}
