import React, { useState } from 'react'
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';

export const LoginForm = () => {
    const [inputFullName, setInputFullName] = useState<string>('');
    const [inputPhone, setInputPhone] = useState<string>('');
    const [inputEmail, setInputEmail] = useState<string>('');

  return (
    <div>
        <h3>Get in touch with us</h3>
        <form>
            <label htmlFor="">Full name</label>
            <div className="">
                <label htmlFor='inputLoginFullName'>Full Name</label>
                    <input type="text" placeholder='Full name' id='inputLoginFullName' required value={inputFullName} onChange={(e) => setInputFullName(e.target.value)}/>
            </div>
            <div>
            <PhoneInput
                country={'ru'} // Страна по умолчанию, например 'ru' для России
                value={inputPhone}
                onChange={setInputPhone} // Обновление состояния при изменении телефона
                onlyCountries={['ru', 'by', 'us', 'gb', 'de']} // Только определённые страны, если хотите ограничить
                inputStyle={{
                width: '300px', // Ширина поля ввода
                height: '40px',
                borderRadius: '8px',
                border: '1px solid #E2E8F0',
                backgroundColor: '#F9FAFB',
                fontSize: '16px',
                }}
                buttonStyle={{
                borderRadius: '8px',
                border: '1px solid #E2E8F0',
                backgroundColor: '#F9FAFB',
                }}
                dropdownStyle={{
                borderRadius: '8px',
                backgroundColor: '#fff',
                }}
            />
            </div>
            {/* <div className="">
                <label htmlFor='inputLoginPhone'>Phone</label>
                    <input type="text" id='inputLoginPhone' required value={inputMail} onChange={(e) => setMail(e.target.value)}/>
            </div> */}
            {/* <div className="form-group mt-3">
                <label htmlFor='inputLoginFullName'>Full Name</label>
                    <input type="text" id='inputLoginFullName' required value={inputMail} onChange={(e) => setMail(e.target.value)}/>
            </div>
            <div className="form-group mt-3">
                <label htmlFor='inputLoginFullName'>Full Name</label>
                    <input type="text" id='inputLoginFullName' required value={inputMail} onChange={(e) => setMail(e.target.value)}/>
            </div> */}
        </form>
    </div>
  )
}
